function y = eggholderg(x)
    y = [-sin(sqrt(abs(-x(1)+x(2)+47)))-(x(1)*(x(1)-x(2)-47)*cos(sqrt(abs(-x(1)+x(2)+47))))/(2*abs(-x(1)+x(2)+47)^(3/2))-((x(2)+47)*(x(1)+2*x(2)+94)* cos(sqrt(abs(x(1)/2+x(2)+47))))/(8* abs(x(1)/2+x(2)+47)^(3/2));
         1/2 * (-2 * sin(sqrt(abs(x(1)+x(2)/2+47)))+(x(2) * (-x(1)+x(2)-47) * cos(sqrt(abs(x(1)-x(2)+47))))/abs(x(1)-x(2)+47)^(3/2)-((x(1)+47)*(2* x(1)+x(2)+94)* cos(sqrt(abs(x(1)+x(2)/2+47))))/(2* abs(x(1)+x(2)/2+47)^(3/2)))];
end